import React, { useState } from 'react'
import axios from 'axios'
import  {proxyAddr}  from './ProxyAddr'

axios.defaults.withCredentials = true;
import {
  CAvatar,
  CButton,
  CProgress,
  CTable,
  CTableBody,
  CTableDataCell,
  CTableHead,
  CTableHeaderCell,
  CTableRow,
} from '@coreui/react'
import CIcon from '@coreui/icons-react'
import {
  cibCcMastercard,
  cifUs,
  cilPeople,
  cibTwitter,
} from '@coreui/icons'

import avatar1 from 'src/assets/images/avatars/1.jpg'

import { Radar } from "react-chartjs-2";
import { Chart, registerables } from 'chart.js';
Chart.register(...registerables);

var radarDataset = []
export const DrawUser = () => {
  const [profile, setProfile] = useState([])
  const url = proxyAddr()

  if (radarDataset.length === 0){
    axios
      .get(`${url}user-profile/get`)
      .then((response) => {
        // dataset [{user, data }, ]
        radarDataset = []
        const profileDataset = response.data.dataset
        profileDataset.map((item, index) => (
          radarDataset.push(
            {
              avatar: { src: avatar1, status: 'success' },
              user: {
                name: item.user,
                new: true,
                registered: 'Jan 1, 2021',
                posturl: item.posturl,
              },
              country: { name: 'USA', flag: cifUs },
              usage: {
                value: 50,
                period: 'Jun 11, 2021 - Jul 10, 2021',
                color: 'success',
              },
              payment: { name: 'Mastercard', icon: cibCcMastercard },
              activity: '10 sec ago',
              labels: item.labels,
              data: item.data
            }
          )
        ))
        console.log(radarDataset)
        setProfile(radarDataset)
      })
      .catch((error) => console.error(`Error: ${error}`))
  }


  return (
    <CTable align="middle" className="mb-0 border" hover responsive>
      <CTableHead color="light">
        <CTableRow>
          <CTableHeaderCell className="text-center">
            <CIcon icon={cilPeople} />
          </CTableHeaderCell>
          <CTableHeaderCell>User</CTableHeaderCell>
          <CTableHeaderCell className="text-center">Country</CTableHeaderCell>
          <CTableHeaderCell>Usage</CTableHeaderCell>
        </CTableRow>
      </CTableHead>
      <CTableBody>
        {profile.map((item, index) => (
          <CTableRow v-for="item in tableItems" key={index}>
            <CTableDataCell className="text-center">
              <CAvatar size="md" src={item.avatar.src} status={item.avatar.status} />
            </CTableDataCell>
            <CTableDataCell>
              <div>{item.user.name}</div>
              <div className="small text-medium-emphasis">
                <span>{item.user.new ? 'New' : 'Recurring'}</span> | Registered:{' '}
                {item.user.registered}
              </div>
              <CButton color="#00aced" onClick={() => window.open(item.user.posturl)} className="float-end">
                <CIcon icon={cibTwitter} />see user
              </CButton>
            </CTableDataCell>
            <CTableDataCell className="text-center">
              <CIcon size="xl" icon={item.country.flag} title={item.country.name} />
            </CTableDataCell>
            <CTableDataCell>
              <div className="clearfix">
                <div className="float-start">
                  <strong>{item.usage.value}%</strong>
                </div>
                <div className="float-end">
                  <small className="text-medium-emphasis">{item.usage.period}</small>
                </div>
              </div>
                <div className="hi">
                  <h1>
                    <Radar
                      data={{
                        labels: item.labels,
                        datasets: [
                          {
                            label: "March",
                            backgroundColor: "rgba(34, 202, 236, .2)",
                            borderColor: "rgba(34, 202, 236, 1)",
                            pointBackgroundColor: "rgba(34, 202, 236, 1)",
                            poingBorderColor: "#fff",
                            pointHoverBackgroundColor: "#fff",
                            pointHoverBorderColor: "rgba(34, 202, 236, 1)",
                            data: item.data
                          }
                        ]
                      }}
                      option={{
                        scale: {
                          ticks: {
                            min: 0.0,
                            max: 1.0,
                            stepSize: 0.1,
                            showLabelBackdrop: false,
                            backdropColor: "rgba(203, 197, 11, 1)"
                          },
                          angleLines: {
                            color: "rgba(255, 255, 255, .3)",
                            lineWidth: 1
                          },
                          gridLines: {
                            color: "rgba(255, 255, 255, .3)",
                            circular: true
                          }
                        }
                      }}
                    ></Radar>
                  </h1>
                </div>
              <CProgress thin color={item.usage.color} value={item.usage.value} />
            </CTableDataCell>
          </CTableRow>
        ))}
      </CTableBody>
    </CTable>
  )
}
